// File: client/src/routes/ObjectPage.tsx
import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import useFetch from '../hooks/useFetch';
import ChartModal from '../components/ChartModal';
import { Metric } from '../config/thresholds';
import { ObjectItem } from '../types';

type Params = { objectId: string };

const ObjectPage: React.FC = () => {
  const { objectId } = useParams<Params>();
  const navigate = useNavigate();
  const { data: object } = useFetch<ObjectItem>(`/api/objects/${objectId}`);
  const [alerts, setAlerts] = useState<Record<Metric, boolean>>({
    voltage: false,
    current: false,
    temperature: false,
  });
  const [activeMetric, setActiveMetric] = useState<Metric | null>(null);

  if (!object) return <div>Загрузка...</div>;

  return (
    <div style={{ padding:20, fontFamily:'Roboto', color:'#FFD014' }}>
      <button onClick={() => navigate(-1)}>← Назад</button>
      <h1>{object.name}</h1>
      <p>{object.description}</p>
      <p><strong>Дата:</strong> {new Date(object.createdAt).toLocaleDateString()}</p>
      <p><strong>Локация:</strong> {object.location}</p>

      <div style={{ display:'flex', gap:16, marginTop:24 }}>
        {(['voltage','current','temperature'] as Metric[]).map(m => (
          <div
            key={m}
            style={{
              flex:1,
              padding:16,
              background:'#111',
              borderRadius:8,
              cursor:'pointer',
              position:'relative'
            }}
            onClick={() => setActiveMetric(m)}
          >
            <h3 style={{ margin:0, textTransform:'capitalize' }}>{m}</h3>
            {alerts[m] && (
              <span style={{ position:'absolute', top:8, right:8 }}>⚠️</span>
            )}
          </div>
        ))}
      </div>

      {activeMetric && (
        <ChartModal
          type={activeMetric}
          objectId={Number(objectId)}
          onClose={() => setActiveMetric(null)}
          onAlert={(metric, has) =>
            setAlerts(a => ({ ...a, [metric]: has }))
          }
        />
      )}
    </div>
  );
};

export default ObjectPage;
