// File: client/src/routes/LocationPage.tsx
import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import useFetch                   from '../hooks/useFetch';

interface ObjectItem {
  id: number;
  name: string;
  unit: string;
  description: string;
  createdAt: string;
}

interface LocationWithObjects {
  location: string;
  objects: ObjectItem[];
}

const LocationPage: React.FC = () => {
  const { location } = useParams();  // без дженерика
  const navigate     = useNavigate();

  const { data: locs, error, loading } =
    useFetch<LocationWithObjects[]>('/api/locations-with-objects');

  if (loading) return <div>Загрузка…</div>;
  if (error)   return <div style={{ color: 'red' }}>Ошибка: {error.message}</div>;

  const loc = locs?.find(l => l.location === location);
  if (!loc) return <div>Локация «{location}» не найдена</div>;

  return (
    <div style={{ padding: 20, fontFamily: 'Roboto, sans-serif' }}>
      <button onClick={() => navigate(-1)} style={{
        marginBottom: 16,
        background: 'transparent',
        border: '1px solid #FFD014',
        color: '#FFD014',
        padding: '8px 12px',
        borderRadius: 4
      }}>← Назад</button>

      <h1 style={{ color: '#FFD014' }}>Локация: {loc.location}</h1>
      <ul>
        {loc.objects.map(obj => (
          <li key={obj.id} style={{ margin: '8px 0', color: '#FFF' }}>
            <strong style={{ color: '#FFD014' }}>{obj.name}</strong><br />
            <span>{obj.description}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LocationPage;
