// client/src/routes/LocationPage.tsx
import React from 'react';
import { useParams, Link } from 'react-router-dom';
import useFetch from '../hooks/useFetch';

type Params = { stationId: string };
type Obj = { id: number; name: string; description: string };

const LocationPage: React.FC = () => {
  const { stationId } = useParams<Params>();
  const sid = Number(stationId);
  const { data, error } = useFetch<Obj[]>(`/api/stations/${sid}/objects`);

  if (!data && !error) return <div>Загрузка...</div>;
  if (error)          return <div>Ошибка: {error.toString()}</div>;

  return (
    <div>
      <Link to="/">← Главная</Link>
      <h1>Станция {sid}</h1>
      <ul>
        {data!.map(o => (
          <li key={o.id}>
            <Link to={`/object/${o.id}`}>{o.name}</Link>
            <p>{o.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LocationPage;
