import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';

interface Props {
  type: 'voltage' | 'current' | 'temperature';
  objectId: number;
  onClose: () => void;
}

const ChartModal: React.FC<Props> = ({ type, objectId, onClose }) => {
  const [data, setData] = useState<any[]>([]);
  useEffect(() => {
    fetch(`/api/objects/${objectId}/data?type=${type}`)
      .then(res => res.json())
      .then(setData);
  }, [objectId, type]);

    const downloadCSV = () => {
    const header = 'timestamp,value\n';
    const rows = data.map(d => `${d.timestamp},${d.value}`).join('\n');
    const blob = new Blob([header + rows], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${type}_${objectId}_data.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <button onClick={onClose}>Ã—</button>
        <h2 style={{color: "black"}}>{type}</h2>
        <LineChart width={1500} height={700} data={data} style={{backgroundColor: '#252525'}}>
          <CartesianGrid strokeDasharray="#444" />
          <XAxis dataKey="timestamp" tick={{fill: '#fff'}} stroke='#white' strokeWidth={2} fontFamily='14px, bold, Roboto, black,'/>
          <YAxis stroke='#fff' tick={{fill: '#fff'}} strokeWidth={2} fontFamily='Roboto, black, bold' />
          <Tooltip wrapperStyle={{backgroundColor: 'black', borderColor: 'black'}} labelStyle={{color: '#ffd014', fontFamily: 'Roboto, bold'}}  contentStyle={{ backgroundColor:'#252525', border:'2px solid #ffd014', fontFamily:'Roboto, bold, white'  }}/>
          <Line type="monotone" stroke='#FFD014' dataKey="value" />
        </LineChart>
      </div>
    </div>
  );
};
export default ChartModal;
