import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Header               from './components/Header';
import MainScreen           from './routes/MainScreen';
import ObjectSelectionPage  from './routes/ObjectSelectionPage';
import ObjectPage           from './routes/ObjectPage';
import ObjectInfoPage       from './routes/ObjectInfoPage';
import LocationPage         from './routes/LocationPage'; 

const App: React.FC = () => (
  <>
    <Header />
    <div style={{ paddingTop: 100 }}>
      <Routes>
      <Route path="/" element={<MainScreen />} />

      {/* маршрут для списка объектов конкретной станции */}
      <Route
        path="/station/:stationId/objects"
        element={<LocationPage />}
      />

      {/* если у вас был промежуточный экран "выбора объекта", например */}
      <Route
        path="/station/:stationId/select"
        element={<ObjectSelectionPage />}
      />

      {/* страница с графиком/таблицей одного объекта */}
      <Route
        path="/object/:objectId"
        element={<ObjectPage />}
      />

      {/* подробная информация об объекте */}
      <Route
        path="/object/:objectId/info"
        element={<ObjectInfoPage />}
      />

      {/* не обязательный «404» */}
      <Route
        path="*"
        element={<div>Страница не найдена</div>}
      />
    </Routes>
    </div>
  </>
);

export default App;
