// File: client/src/routes/MainScreen.tsx
import React from 'react';
import { useNavigate }     from 'react-router-dom';
import useFetch            from '../hooks/useFetch';

interface LocationWithObjects {
  location: string;
  objects: {
    id: number;
    name: string;
    unit: string;
    description: string;
    createdAt: string;
  }[];
}

const MainScreen: React.FC = () => {
  const navigate = useNavigate();
  const { data: locations, error, loading } = useFetch<LocationWithObjects[]>('/api/locations-with-objects');

  if (loading) return <div>Загрузка локаций…</div>;
  if (error)   return <div style={{ color: 'red' }}>Ошибка загрузки: {error.message}</div>;
  if (!locations || locations.length === 0) {
    return <div>Локации не найдены.</div>;
  }

  return (
    <div style={{ padding: 20, fontFamily: 'Roboto, sans-serif' }}>
      <h1 style={{ color: '#FFD014' }}>Список локаций</h1>
      <div style={{
        display: 'grid',
        gap: 16,
        gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))'
      }}>
        {locations.map(loc => (
          <div
            key={loc.location}
            style={{
              background: '#252525',
              border: '1px solid #FFD014',
              borderRadius: 8,
              padding: 12,
              cursor: 'pointer',
            }}
            onClick={() => navigate(`/location/${encodeURIComponent(loc.location)}`)}
          >
            <h2 style={{ margin: '0 0 8px', color: '#FFD014' }}>{loc.location}</h2>
            <ul style={{ margin: 0, paddingLeft: 20 }}>
              {loc.objects.map(obj => (
                <li key={obj.id} style={{ color: '#FFF' }}>
                  {obj.name}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MainScreen;
