import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ObjectCard from '../components/ObjectCard';
import ChartModal from '../components/ChartModal';
import useFetch from '../hooks/useFetch';
import { ObjectItem } from '../types';

type Params = { objectId: string };

const ObjectPage: React.FC = () => {
  const { objectId } = useParams<Params>();
  const navigate = useNavigate();
  const { data: object } = useFetch<ObjectItem>(`/api/objects/${objectId}`);
  const [modalType, setModalType] = useState<'voltage'|'current'|'temperature'|null>(null);

  if (!object) return <div>Загрузка...</div>;

  return (
    <div>
      <button onClick={() => navigate(-1)}>← Назад</button>
      <h1>{object.name}</h1>
      <ObjectCard object={object} />
      <button onClick={() => setModalType('voltage')}>Напряжение</button>
      <button onClick={() => setModalType('current')}>Ток</button>
      <button onClick={() => setModalType('temperature')}>Температура</button>
      {modalType && (
        <ChartModal type={modalType} objectId={Number(objectId)} onClose={() => setModalType(null)} />
      )}
    </div>
  );
};

export default ObjectPage;
