// client/src/routes/MainScreen.tsx
import React from 'react';
import { Link } from 'react-router-dom';
import useFetch from '../hooks/useFetch';

type Station = { id: number; name: string };

const MainScreen: React.FC = () => {
  const { data, error } = useFetch<Station[]>('/api/stations');
  if (!data && !error) return <div>Загрузка...</div>;
  if (error)         return <div>Ошибка: {error.toString()}</div>;

  return (
    <div>
      <h1>Станции</h1>
      <ul>
        {data!.map(st => (
          <li key={st.id}>
            {/* Путь точно совпадает с тем, что прописано в App.tsx */}
            <Link to={'/station/:stationId/objects'}>
              {st.name}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MainScreen;
