// File: client/src/components/ChartModal.tsx
import React, { useState, useEffect } from 'react';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ReferenceLine
} from 'recharts';
import { thresholds, Metric, Threshold } from '../config/thresholds';

interface Props {
  type: Metric;
  objectId: number;
  onClose: () => void;
  onAlert: (type: Metric, hasAlert: boolean) => void;  
}

interface DataPoint {
  timestamp: string;
  time:      number;  // ms
  value:     number;
  outOfBounds: boolean;
  resolved:     boolean;
}

const ChartModal: React.FC<Props> = ({ type, objectId, onClose, onAlert }) => {
  const [data, setData] = useState<DataPoint[]>([]);
  const [selectedPoint, setSelectedPoint] = useState<DataPoint | null>(null);

  // Получаем текущий порог
  const { min, max }: Threshold = thresholds[type];

  useEffect(() => {
    // запросим данные
    fetch(`/api/objects/${objectId}/data?type=${type}`)
      .then(r => r.json())
      .then((raw: Array<{ timestamp: string; value: number }>) => {
        const enriched = raw.map(p => {
          const time = new Date(p.timestamp).getTime();
          const oob  = p.value < min || p.value > max;
          return { ...p, time, outOfBounds: oob, resolved: false };
        });
        setData(enriched);
        // если хоть одна точка за пределом — поднимаем тревогу
        onAlert(type, enriched.some(p => p.outOfBounds && !p.resolved));
      })
      .catch(console.error);
  }, [objectId, type, min, max, onAlert]);

  const handleAction = (action: 'delete' | 'resolve' | 'ignore') => {
    if (!selectedPoint) return;
    setData(d =>
      d
        .filter(pt => !(action === 'delete' && pt === selectedPoint))
        .map(pt =>
          pt === selectedPoint && action === 'resolve'
            ? { ...pt, outOfBounds: false, resolved: true }
            : pt
        )
    );
    setSelectedPoint(null);
    // обновляем тревогу в родителе
    const anyAlertLeft = data
      .filter(pt => action === 'delete' ? pt !== selectedPoint : true)
      .some(pt => pt.outOfBounds && !pt.resolved);
    onAlert(type, anyAlertLeft);
  };

  return (
    <div
      style={{
        position:'fixed', top:0,left:0,right:0,bottom:0,
        background:'rgba(0,0,0,0.8)', display:'flex',
        justifyContent:'center', alignItems:'center', zIndex:10000
      }}
    >
      <div style={{
        position:'relative',
        width: 1600, padding:20,
        background:'#000', color:'#FFD014',
        borderRadius:8, fontFamily:'Roboto'
      }}>
        <button
          onClick={onClose}
          style={{
            position:'absolute', top:10, right:10,
            background:'none', border:'none', color:'#FFD014', fontSize:24
          }}
        >×</button>
        <h2 style={{ marginTop:0, textTransform:'capitalize' }}>{type}</h2>

        <div style={{ display:'flex', gap:20 }}>
          <LineChart
            width={1500}
            height={700}
            data={data}
          >
            <CartesianGrid stroke="#444" />
            <XAxis
              dataKey="time"
              type="number"
              domain={['dataMin','dataMax']}
              tickFormatter={ms => {
                const d = new Date(ms);
                return `${d.getHours()}:${String(d.getMinutes()).padStart(2,'0')}`;
              }}
              tick={{ fill:'#FFD014' }}
            />
            <YAxis tick={{ fill:'#FFD014' }} />
            <Tooltip
              labelFormatter={(ms: number) =>
                new Date(ms).toLocaleString('ru-RU')
              }
              contentStyle={{ background:'#000', borderColor:'#FFD014' }}
              itemStyle={{ color:'#FFD014' }}
            />
            <Line
              dataKey="value"
              stroke="#FFD014"
              dot={dotProps => {
                const { cx, cy, payload } = dotProps;
                const radius = payload.outOfBounds ? 6 : 3;
                const fill   = payload.outOfBounds ? 'red' : '#FFD014';
                return (
                  <circle
                    cx={cx} cy={cy} r={radius}
                    fill={fill}
                    stroke="#FFD014"
                    strokeWidth={payload.outOfBounds ? 2 : 1}
                    style={{ cursor:'pointer' }}
                    onClick={() => setSelectedPoint(payload)}
                  />
                );
              }}
            />
            {selectedPoint && (
              <ReferenceLine
                x={selectedPoint.time}
                stroke="#FFD014"
                strokeWidth={2}
                strokeDasharray="5 5"
              />
            )}
          </LineChart>

          <div
            style={{
              width: 300,
              height: 700,
              overflowY: 'auto',
              background: '#000',
              border: '1px solid #444'
            }}
          >
            <table style={{ borderCollapse:'collapse', width:'100%' }}>
              <thead>
                <tr>
                  <th style={{ border:'1px solid #444', padding:4 }}>Время</th>
                  <th style={{ border:'1px solid #444', padding:4 }}>Значение</th>
                </tr>
              </thead>
              <tbody>
                {data.map((d,i) => (
                  <tr key={i}>
                    <td style={{ border:'1px solid #444', padding:4 }}>
                      {new Date(d.timestamp).toLocaleString('ru-RU', {
                        day:'2-digit',month:'2-digit',year:'numeric',
                        hour:'2-digit',minute:'2-digit'
                      })}
                    </td>
                    <td style={{ border:'1px solid #444', padding:4 }}>
                      {d.value}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {selectedPoint && (
          <div style={{
            position:'absolute', top:100, left:100,
            background:'#222', border:'1px solid #FFD014',
            padding:16, borderRadius:8
          }}>
            <p>
              <strong>
                {new Date(selectedPoint.timestamp).toLocaleString('ru-RU')}
              </strong>:&nbsp;
              {selectedPoint.value}
            </p>
            <button onClick={() => handleAction('delete')}>
              Удалить
            </button>
            <button onClick={() => handleAction('resolve')} style={{ marginLeft:8 }}>
              Решено (серая)
            </button>
            <button onClick={() => setSelectedPoint(null)} style={{ marginLeft:8 }}>
              Отмена
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChartModal;
