// client/src/App.tsx
import React from 'react'
import { BrowserRouter, Routes, Route } from 'react-router-dom'

import MainScreen          from './routes/MainScreen'
import LocationPage        from './routes/LocationPage'
import ObjectSelectionPage from './routes/ObjectSelectionPage'
import ObjectPage          from './routes/ObjectPage'
import ObjectInfoPage      from './routes/ObjectInfoPage'

function App() {
  return (
    <BrowserRouter>
      <Routes>
        {/* 1) Главная страница */}
        <Route path="/" element={<MainScreen />} />

        {/* 2) Страница локации по имени */}
        <Route path="/location/:location" element={<LocationPage />} />

        {/* 3) Если нужен отдельный экран селекта */}
        <Route
          path="/location/:location/select"
          element={<ObjectSelectionPage />}
        />

        {/* 4) Страница конкретного объекта */}
        <Route path="/object/:objectId" element={<ObjectPage />} />
        <Route path="/object/:objectId/info" element={<ObjectInfoPage />} />
      </Routes>
    </BrowserRouter>
  )
}

export default App
