import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts';

interface Props {
  type: 'voltage' | 'current' | 'temperature';
  objectId: number;
  onClose: () => void;
}

const ChartModal: React.FC<Props> = ({ type, objectId, onClose }) => {
  const [data, setData] = useState<any[]>([]);
  useEffect(() => {
    fetch(`/api/objects/${objectId}/data?type=${type}`)
      .then(res => res.json())
      .then(setData);
  }, [objectId, type]);

  return (
    <div className="modal">
      <div className="modal-content">
        <button onClick={onClose}>Ã—</button>
        <h2>{type}</h2>
        <LineChart width={1500} height={700} data={data}>
          <CartesianGrid strokeDasharray="0 0" />
          <XAxis dataKey="timestamp" stroke='white' strokeWidth={2} fontFamily='14px, bold, Roboto, black,'/>
          <YAxis stroke='white' strokeWidth={2} fontFamily='Roboto, black, bold' />
          <Tooltip wrapperStyle={{backgroundColor: 'black', borderColor: 'black'}}  contentStyle={{ backgroundColor:'#252525', border:'2px solid #ffd014', fontFamily:'Roboto, bold, white'  }}/>
          <Line type="monotone" dataKey="value" stroke="#8884d8" />
        </LineChart>
      </div>
    </div>
  );
};
export default ChartModal;
