// File: client/src/components/ChartModal.tsx
import React, { useState, useEffect } from 'react';
import {
  LineChart, Line,
  XAxis, YAxis,
  CartesianGrid, Tooltip
} from 'recharts';

interface Props {
  type: 'voltage' | 'current' | 'temperature';
  objectId: number;
  onClose: () => void;
}

export const ChartModal: React.FC<Props> = ({ type, objectId, onClose }) => {
  // Параметры диапазона и интервала
  const [range, setRange] = useState<number>(24);        // часы
  const [interval, setInterval] = useState<number>(60);  // минуты

  const [data, setData] = useState<{ timestamp: string; value: number }[]>([]);

  useEffect(() => {
    // Формируем query параметры
    const params = new URLSearchParams();
    if (range <= 24) params.set('rangeHours', range.toString());
    else params.set('rangeDays', (range / 24).toString());
    if (interval < 60) {
      params.set('intervalMinutes', interval.toString());
    } else {
      params.set('intervalHours', (interval / 60).toString());
    }
    params.set('type', type);

    fetch(`/api/objects/${objectId}/data?${params.toString()}`)
      .then(res => res.json())
      .then(setData);
  }, [objectId, type, range, interval]);

  // Скачивание CSV с разделителем ';'
  const downloadCSV = () => {
    const BOM = '\uFEFF';
    const header = 'timestamp;value\n';
    const rows = data.map(d => `${d.timestamp};${d.value}`).join('\n');
    const blob = new Blob([BOM + header + rows], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${type}_${objectId}_data.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="modal">
      <div className="modal-content" style={{ background: '#000', color: '#FFD014', fontFamily: 'Roboto', padding: 20 }}>
        <button onClick={onClose} style={{ float: 'right', fontSize: 18, background: 'transparent', border: 'none', color: '#FFD014' }}>×</button>
        <h2>{type.toUpperCase()}</h2>

        {/* Управление диапазоном */}
        <div style={{ display: 'flex', gap: 16, marginBottom: 16 }}>
          <label>
            Диапазон (часы):
            <select value={range} onChange={e => setRange(+e.target.value)} style={{ marginLeft: 8 }}>
              <option value={1}>1</option>
              <option value={6}>6</option>
              <option value={12}>12</option>
              <option value={24}>24</option>
              <option value={168}>168 (7 дней)</option>
            </select>
          </label>
          <label>
            Интервал (мин):
            <select value={interval} onChange={e => setInterval(+e.target.value)} style={{ marginLeft: 8 }}>
              <option value={1}>1</option>
              <option value={5}>5</option>
              <option value={15}>15</option>
              <option value={60}>60</option>
            </select>
          </label>
          <button onClick={downloadCSV} style={{ background: '#222', border: '1px solid #FFD014', color: '#FFD014', padding: '4px 8px', cursor: 'pointer' }}>
            Скачать CSV
          </button>
        </div>

        <div style={{ display: 'flex', gap: 20 }}>
          {/* График */}
          <LineChart width={600} height={300} data={data} style={{ backgroundColor: '#000' }}>
            <CartesianGrid stroke="#444" />
            <XAxis dataKey="timestamp" tick={{ fill: '#FFD014' }} />
            <YAxis tick={{ fill: '#FFD014' }} />
            <Tooltip
              contentStyle={{ backgroundColor: '#000', borderColor: '#FFD014' }}
              labelStyle={{ color: '#FFD014' }}
              itemStyle={{ color: '#FFD014' }}
            />
            <Line type="monotone" dataKey="value" stroke="#FFD014" dot={{ r: 3 }} />
          </LineChart>

          {/* Таблица данных */}
          <div style={{ maxHeight: 300, overflowY: 'auto' }}>
            <table style={{ borderCollapse: 'collapse', width: '100%' }}>
              <thead>
                <tr>
                  <th style={{ border: '1px solid #444', padding: 4 }}>Время</th>
                  <th style={{ border: '1px solid #444', padding: 4 }}>Значение</th>
                </tr>
              </thead>
              <tbody>
                {data.map((d, i) => (
                  <tr key={i}>
                    <td style={{ border: '1px solid #444', padding: 4 }}>{d.timestamp}</td>
                    <td style={{ border: '1px solid #444', padding: 4 }}>{d.value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChartModal;
