// File: client/src/routes/MainScreen.tsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import useFetch from '../hooks/useFetch';
import { Station } from '../types';

const MainScreen: React.FC = () => {
  const navigate = useNavigate();
  const { data: stations } = useFetch<Station[]>('/api/stations');

  if (!stations) return <div>Загрузка…</div>;

  return (
    <div style={{ padding:20, fontFamily:'Roboto', color:'#FFD014' }}>
      <h1>Станции</h1>
      <div style={{
        display:'grid',
        gridTemplateColumns:'repeat(auto-fill,minmax(200px,1fr))',
        gap:16
      }}>
        {stations.map(s => (
          <div
            key={s.name}
            onClick={() =>
              navigate(`/location/${encodeURIComponent(s.name)}/select`)
            }
            style={{
              padding:16,
              background:'#111',
              borderRadius:8,
              cursor:'pointer',
              position:'relative'
            }}
          >
            <h2 style={{ margin:0 }}>{s.name}</h2>
            {s.hasAlert && <span style={{
              position:'absolute', top:8, right:8
            }}>⚠️</span>}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MainScreen;
