// File: client/src/components/ChartModal.tsx

import React, { useState, useEffect, useMemo } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
} from 'recharts';
import useFetch from '../hooks/useFetch';
import './ChartModal.css';

type Metric = 'voltage' | 'current' | 'temperature';

interface DataPoint {
  timestamp: string;
  value: string | number | null;
}

interface ChartModalProps {
  visible: boolean;
  onClose: () => void;
  objectId: number;
  type: Metric;
  onAlert?: (timestamp: string, value: number) => void;
}

const ChartModal: React.FC<ChartModalProps> = ({
  visible,
  onClose,
  objectId,
  type,
  onAlert,
}) => {
  // Если не видно — не рендерим
  if (!visible) return null;

  // Закрытие по Esc
  useEffect(() => {
    const handler = (e: KeyboardEvent) => {
      if (e.key === 'Escape') onClose();
    };
    window.addEventListener('keydown', handler);
    return () => window.removeEventListener('keydown', handler);
  }, [onClose]);

  const titleMap: Record<Metric, string> = {
    voltage:     'Напряжение / Время',
    current:     'Ток / Время',
    temperature: 'Температура / Время',
  };
  const title = titleMap[type];

  // По умолчанию — последние 24 часа
  const now    = new Date();
  const ago24h = new Date(now.getTime() - 24 * 60 * 60 * 1000);
  const fmt = (d: Date) => d.toISOString().slice(0, 16);

  const [start, setStart]             = useState<string>(fmt(ago24h));
  const [end, setEnd]                 = useState<string>(fmt(now));
  const [intervalMin, setIntervalMin] = useState<number>(5);
  const [minBound, setMinBound]       = useState<string>('');
  const [maxBound, setMaxBound]       = useState<string>('');

  const query = `?start=${encodeURIComponent(start)}&end=${encodeURIComponent(end)}&intervalMinutes=${intervalMin}&type=${type}`;
  const { data /*, error */ } = useFetch<DataPoint[]>(`/api/objects/${objectId}/data${query}`);
  if (!data) {
    return (
      <div className="modal-overlay">
        <div className="modal-window">
          <div style={{ color: '#FFD014', fontFamily: 'Roboto' }}>
            Загрузка данных…
          </div>
        </div>
      </div>
    );
  }

  const csvContent = useMemo(() => {
    if (!data.length) return '';
    const header = ['timestamp','value'];
    const rows = data.map(d => [d.timestamp, d.value != null ? String(d.value) : '']);
    return [header, ...rows].map(r => r.join(',')).join('\n');
  }, [data]);

  const downloadCsv = () => {
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url  = URL.createObjectURL(blob);
    const a    = document.createElement('a');
    a.href     = url;
    a.download = `${type}_${start}_${end}.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    // Клик по оверлею закрывает
    <div className="modal-overlay" onClick={onClose}>
      {/* Предотвращаем закрытие при клике внутри окна */}
      <div
        className="modal-window"
        onClick={e => e.stopPropagation()}
        style={{ position: 'relative' }}
      >
        <button
          className="close-button"
          onClick={onClose}
          aria-label="Закрыть"
        >
          ×
        </button>

        <h2 className="modal-title">{title}</h2>

        <div className="modal-controls">
          <label>
            С:{' '}
            <input
              type="datetime-local"
              value={start}
              onChange={e => setStart(e.target.value)}
            />
          </label>
          <label>
            По:{' '}
            <input
              type="datetime-local"
              value={end}
              onChange={e => setEnd(e.target.value)}
            />
          </label>
          <label>
            Интервал (мин):{' '}
            <input
              type="number"
              min={1}
              value={intervalMin}
              onChange={e => setIntervalMin(Number(e.target.value))}
            />
          </label>
          <label>
            Мин. граница:{' '}
            <input
              type="text"
              value={minBound}
              onChange={e => setMinBound(e.target.value)}
              placeholder="необязательно"
            />
          </label>
          <label>
            Макс. граница:{' '}
            <input
              type="text"
              value={maxBound}
              onChange={e => setMaxBound(e.target.value)}
              placeholder="необязательно"
            />
          </label>
          <button className="csv-button" onClick={downloadCsv}>
            Скачать CSV
          </button>
        </div>

        <div className="chart-table-container">
          <LineChart
            width={1500}
            height={700}
            data={data}
            style={{ backgroundColor: '#000', fontFamily: 'Roboto, sans-serif' }}
          >
            <CartesianGrid stroke="#444" />
            <XAxis
              dataKey="timestamp"
              stroke="#FFD014"
              tickFormatter={str => {
                const d = new Date(str);
                return `${d.getHours()}:${d.getMinutes().toString().padStart(2,'0')}`;
              }}
            />
            <YAxis stroke="#FFD014" />
            <Tooltip
              contentStyle={{
                backgroundColor: '#000',
                borderColor: '#FFD014',
                fontFamily: 'Roboto, sans-serif',
              }}
              labelFormatter={l => new Date(l).toLocaleString()}
              formatter={val => {
                const num = val == null ? NaN : Number(val);
                return [isNaN(num) ? '—' : num.toFixed(2), ''];
              }}
            />
            <Line
              type="monotone"
              dataKey="value"
              stroke="#FFD014"
              dot={dotProps => {
                const { cx, cy, payload, index } = dotProps;
                const key = `${payload.timestamp}-${index}`;
                const num = payload.value == null ? NaN : Number(payload.value);
                const isOut = 
                  (minBound && !isNaN(num) && num < Number(minBound)) ||
                  (maxBound && !isNaN(num) && num > Number(maxBound));
                if (isOut && onAlert) {
                  onAlert(payload.timestamp, num);
                }
                return (
                  <circle
                    key={key}
                    cx={cx}
                    cy={cy}
                    r={isOut ? 6 : 4}
                    fill={isOut ? 'red' : '#FFD014'}
                    stroke="#FFD014"
                    style={{ cursor: 'pointer' }}
                  />
                );
              }}
            />
          </LineChart>

          <div
            className="data-table"
            style={{
              height: 700,
              overflowY: 'auto',
              width: 300,
            }}
          >
            <table>
              <thead>
                <tr>
                  <th style={{ color: '#FFD014' }}>Время</th>
                  <th style={{ color: '#FFD014' }}>Значение</th>
                </tr>
              </thead>
              <tbody>
                {data.map((d, i) => {
                  const num = d.value == null ? NaN : Number(d.value);
                  return (
                    <tr key={`${d.timestamp}-${i}`}>
                      <td style={{ color: '#FFD014' }}>
                        {new Date(d.timestamp).toLocaleString()}
                      </td>
                      <td style={{ color: '#FFD014' }}>
                        {isNaN(num) ? '—' : num.toFixed(2)}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChartModal;
