// File: client/src/components/ChartModal.tsx

import React, { useState, useEffect, useMemo } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ReferenceLine,
} from 'recharts';
import useFetch from '../hooks/useFetch';
import './ChartModal.css';

type Metric = 'voltage' | 'current' | 'temperature';

interface DataPoint {
  timestamp: string;
  value: number | null;
}

interface ChartModalProps {
  visible: boolean;
  onClose: () => void;
  objectId: number;
  /** какой из трёх графиков показываем */
  type: Metric;
  /** колбэк при выходе за границы, получает timestamp и value */
  onAlert?: (timestamp: string, value: number) => void;
}

const ChartModal: React.FC<ChartModalProps> = ({
  visible,
  onClose,
  objectId,
  type,
  onAlert,
}) => {
  // Названия графиков
  const titleMap: Record<Metric, string> = {
    voltage:     'Напряжение / Время',
    current:     'Ток / Время',
    temperature: 'Температура / Время',
  };
  const title = titleMap[type];

  // Временные контролы: по умолчанию 24 часа
  const now     = new Date();
  const ago24h  = new Date(now.getTime() - 24 * 60 * 60 * 1000);
  const fmtLocal = (d: Date) => d.toISOString().slice(0, 16);

  const [start, setStart]           = useState<string>(fmtLocal(ago24h));
  const [end, setEnd]               = useState<string>(fmtLocal(now));
  const [intervalMin, setIntervalMin] = useState<number>(5);
  const [minBound, setMinBound]     = useState<string>('');
  const [maxBound, setMaxBound]     = useState<string>('');

  // Запрос данных
  const query = `?start=${encodeURIComponent(start)}&end=${encodeURIComponent(end)}&intervalMinutes=${intervalMin}&type=${type}`;
  const { data: rawData } = useFetch<DataPoint[]>(`/api/objects/${objectId}/data${query}`);
  const [data, setData]   = useState<DataPoint[]>([]);

  useEffect(() => {
    if (rawData) setData(rawData);
  }, [rawData]);

  // CSV-генерация
  const csvContent = useMemo(() => {
    if (!data.length) return '';
    const header = ['timestamp','value'];
    const rows = data.map(d => [d.timestamp, d.value != null ? d.value.toString() : '']);
    return [header, ...rows].map(r => r.join(',')).join('\n');
  }, [data]);

  const downloadCsv = () => {
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url  = URL.createObjectURL(blob);
    const a    = document.createElement('a');
    a.href     = url;
    a.download = `${type}_${start}_${end}.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  if (!visible) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-window">
        <button className="close-button" onClick={onClose}>×</button>
        <h2 className="modal-title">{title}</h2>

        <div className="modal-controls">
          <label>
            С:{' '}
            <input
              type="datetime-local"
              value={start}
              onChange={e => setStart(e.target.value)}
            />
          </label>
          <label>
            По:{' '}
            <input
              type="datetime-local"
              value={end}
              onChange={e => setEnd(e.target.value)}
            />
          </label>
          <label>
            Интервал (мин):{' '}
            <input
              type="number"
              min={1}
              value={intervalMin}
              onChange={e => setIntervalMin(Number(e.target.value))}
            />
          </label>
          <label>
            Мин. граница:{' '}
            <input
              type="text"
              value={minBound}
              onChange={e => setMinBound(e.target.value)}
              placeholder="необязательно"
            />
          </label>
          <label>
            Макс. граница:{' '}
            <input
              type="text"
              value={maxBound}
              onChange={e => setMaxBound(e.target.value)}
              placeholder="необязательно"
            />
          </label>
          <button className="csv-button" onClick={downloadCsv}>
            Скачать CSV
          </button>
        </div>

        <div className="chart-table-container">
          <LineChart
            width={1500}
            height={700}
            data={data}
            style={{ backgroundColor: '#000', fontFamily: 'Roboto, sans-serif' }}
          >
            <CartesianGrid stroke="#444" />
            <XAxis
              dataKey="timestamp"
              stroke="#FFD014"
              tickFormatter={str => {
                const d = new Date(str);
                return `${d.getDate().toString().padStart(2,'0')}.${(d.getMonth()+1).toString().padStart(2,'0')} ${d.getHours().toString().padStart(2,'0')}:${d.getMinutes().toString().padStart(2,'0')}`;
              }}
            />
            <YAxis stroke="#FFD014" />
            <Tooltip
              contentStyle={{
                backgroundColor: '#000',
                borderColor: '#FFD014',
                fontFamily: 'Roboto, sans-serif',
              }}
              labelFormatter={label => new Date(label).toLocaleString()}
              formatter={value => [(value as number)?.toFixed(2), '']}
            />
            <Line
              type="monotone"
              dataKey="value"
              stroke="#FFD014"
              dot={dotProps => {
                const { cx, cy, payload, index } = dotProps;
                const key = `${payload.timestamp}-${index}`;
                const val = payload.value as number | null;
                const isOut = (minBound && val != null && val < +minBound)
                           || (maxBound && val != null && val > +maxBound);
                if (isOut && onAlert) {
                  onAlert(payload.timestamp, val!);
                }
                return (
                  <circle
                    key={key}
                    cx={cx}
                    cy={cy}
                    r={isOut ? 6 : 4}
                    fill={isOut ? 'red' : '#FFD014'}
                    stroke="#FFD014"
                    style={{ cursor: 'pointer' }}
                  />
                );
              }}
            />
            { /* Для выбранной точки можно добавить ReferenceLine */}
          </LineChart>

          <div className="data-table">
            <table>
              <thead>
                <tr>
                  <th style={{ color: '#FFD014' }}>Время</th>
                  <th style={{ color: '#FFD014' }}>Значение</th>
                </tr>
              </thead>
              <tbody>
                {data.map((d, i) => (
                  <tr key={`${d.timestamp}-${i}`}>
                    <td style={{ color: '#FFD014' }}>
                      {new Date(d.timestamp).toLocaleString()}
                    </td>
                    <td style={{ color: '#FFD014' }}>
                      {d.value != null ? d.value.toFixed(2) : '—'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChartModal;
