import React from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import useFetch from '../hooks/useFetch';
import { ObjectItem } from '../types';

// Параметры URL (v6)
type Params = { objectId: string; }

const metricTypes = [
  { key: 'voltage', label: 'Напряжение' },
  { key: 'current', label: 'Ток' },
  { key: 'temperature', label: 'Температура' },
];

const ObjectPage: React.FC = () => {
  const { objectId } = useParams<Params>();
  const navigate = useNavigate();
  const { data: object } = useFetch<ObjectItem>(`/api/objects/${objectId}`);

  if (!object) {
    return (
      <div style={{ fontFamily: 'Roboto, sans-serif', color: '#FFD014', backgroundColor: '#000', padding: 20 }}>
        Загрузка...
      </div>
    );
  }

  return (
    <div style={{
      fontFamily: 'Roboto, sans-serif',
      backgroundColor: '#000',
      color: '#FFD014',
      minHeight: '100vh',
      padding: '20px'
    }}>
      <button
        onClick={() => navigate(-1)}
        style={{
          background: 'transparent',
          border: '1px solid #FFD014',
          borderRadius: '4px',
          color: '#FFD014',
          padding: '8px 12px',
          cursor: 'pointer',
          marginBottom: '16px'
        }}
      >
        ← Назад
      </button>

      <h1 style={{ marginBottom: '8px' }}>{object.name}</h1>
      <p style={{ margin: '4px 0' }}><strong>Описание:</strong> {object.description}</p>
      <p style={{ margin: '4px 0' }}><strong>Дата создания:</strong> {new Date(object.createdAt).toLocaleDateString()}</p>
      <p style={{ margin: '4px 0' }}><strong>Локация:</strong> {object.location}</p>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
        gap: '16px',
        marginTop: '24px'
      }}>
        {metricTypes.map(({ key, label }) => (
          <Link
            key={key}
            to={`/object/${object.id}/info?type=${key}`}
            style={{
              backgroundColor: '#111',
              border: '1px solid #FFD014',
              borderRadius: '8px',
              padding: '16px',
              textDecoration: 'none',
              color: '#FFD014',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              height: '100px'
            }}
          >
            <div style={{ fontSize: '1.1rem', marginBottom: '8px' }}>{label}</div>
            <div style={{ fontSize: '0.9rem' }}>Подробнее →</div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default ObjectPage;
