import React from 'react';
import logo from '../assets/logo.png'; // ваш логотип

const Header: React.FC = () => (
  <header style={{
    position: 'fixed',
    top: 0,
    left: 0,
    height: 60,
    width: '100%',
    backgroundColor: '#252525',  
    display: 'flex',
    alignItems: 'center',
    padding: '0 16px',
    zIndex: 1000
  }}>
    <img
      src={logo}
      alt="Технопогруз Лого"
      style={{ height: 40 /* подберите под свой дизайн */, cursor: 'pointer' }}
      onClick={() => window.location.assign('/')} // клик ведёт на главную
    />
  </header>
);

export default Header;
