import React, { useState, useEffect } from 'react';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip
} from 'recharts';

interface Props {
  type: 'voltage' | 'current' | 'temperature';
  objectId: number;
  onClose: () => void;
  onAlert: (type: 'voltage' | 'current' | 'temperature') => void;
}

interface DataPoint {
  timestamp: string;
  value: number;
  outOfBounds: boolean;
  notified: boolean;
}

const ChartModal: React.FC<Props> = ({
  type, objectId, onClose, onAlert
}) => {
  const nowISO = new Date().toISOString().slice(0,16);
  const [start, setStart] = useState(nowISO.replace(/T.+$/, 'T00:00'));
  const [end, setEnd]     = useState(nowISO);
  const [intervalMinutes, setIntervalMinutes] = useState(5);
  const [minBound, setMinBound] = useState<number | ''>('');
  const [maxBound, setMaxBound] = useState<number | ''>('');
  const [data, setData] = useState<DataPoint[]>([]);
  const [selectedPoint, setSelectedPoint] = useState<DataPoint | null>(null);

  useEffect(() => {
    const params = new URLSearchParams();
    params.set('start', start);
    params.set('end', end);
    params.set('intervalMinutes', String(intervalMinutes));
    params.set('type', type);

    fetch(`/api/objects/${objectId}/data?${params}`)
      .then(r => r.json())
      .then((raw: Array<{timestamp:string,value:number}>) => {
        const withFlags = raw.map(p => {
          const oob = (minBound !== '' && p.value < minBound)
                   || (maxBound !== '' && p.value > maxBound);
          return { ...p, outOfBounds: oob, notified: false };
        });
        setData(withFlags);
        if (withFlags.some(pt => pt.outOfBounds)) {
          onAlert(type);
        }
      });
  }, [start, end, intervalMinutes, type, minBound, maxBound]);

  const downloadCSV = () => {
    const BOM = '\uFEFF';
    const header = 'timestamp;value\n';
    const rows = data.map(d => `${d.timestamp};${d.value}`).join('\n');
    const blob = new Blob([BOM + header + rows], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${type}_${objectId}.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const inputStyle = { background:'#222', color:'#FFD014', border:'1px solid #444', padding:'4px' };

  return (
    <div className="modal" style={{
      position:'fixed', top:0,left:0,right:0,bottom:0,
      background:'#252525', display:'flex', justifyContent:'center', alignItems:'center', zIndex:10000
    }}>
      <div style={{
        background:'#000', color:'#FFD014', fontFamily:'Roboto', padding:20, borderRadius:8, width:'90%', maxWidth:1000
      }}>
        <button onClick={onClose} style={{ float:'right', background:'none', border:'none', color:'#FFD014', fontSize:24 }}>×</button>
        <h2 style={{ marginTop: 0, textTransform:'capitalize' }}>{type}</h2>

        <div style={{ display:'flex', gap:16, marginBottom:16 }}>
          <label>
            С:&nbsp;
            <input type="datetime-local" value={start} onChange={e=>setStart(e.target.value)} style={inputStyle}/>
          </label>
          <label>
            По:&nbsp;
            <input type="datetime-local" value={end} onChange={e=>setEnd(e.target.value)} style={inputStyle}/>
          </label>
          <label>
            Интервал (мин):&nbsp;
            <input type="number" min={1} value={intervalMinutes} onChange={e=>setIntervalMinutes(Number(e.target.value))} style={{...inputStyle,width:60}}/>
          </label>
          <label>
            Мин.Границы:&nbsp;
            <input type="number" value={minBound} onChange={e=>setMinBound(e.target.value===''?'':Number(e.target.value))} style={{...inputStyle,width:80}}/>
          </label>
          <label>
            Макс.Границы:&nbsp;
            <input type="number" value={maxBound} onChange={e=>setMaxBound(e.target.value===''?'':Number(e.target.value))} style={{...inputStyle,width:80}}/>
          </label>
          <button onClick={downloadCSV} style={{ background:'#222', border:'1px solid #FFD014', color:'#FFD014', padding:'4px 8px', cursor:'pointer' }}>
            Скачать CSV
          </button>
        </div>

        <div style={{ display:'flex', gap:20 }}>
          <LineChart width={600} height={300} data={data} onClick={(e:any) => {
            if (e.activePayload?.[0]?.payload.outOfBounds) {
              setSelectedPoint(e.activePayload[0].payload);
            }
          }}>
            <CartesianGrid stroke="#444" />
            <XAxis dataKey="timestamp" tick={{ fill:'#FFD014' }} />
            <YAxis tick={{ fill:'#FFD014' }} />
            <Tooltip contentStyle={{ backgroundColor:'#000', borderColor:'#FFD014' }}
                     labelStyle={{ color:'#FFD014' }} itemStyle={{ color:'#FFD014' }}/>
            <Line dataKey="value" stroke="#FFD014" dot={p => {
              const { cx, cy, payload } = p;
              return payload.outOfBounds
                ? <circle cx={cx} cy={cy} r={6} fill="red" stroke="#FFD014" strokeWidth={2}/>
                : <circle cx={cx} cy={cy} r={3} fill="#FFD014"/>;
            }}/>
          </LineChart>

          <div style={{ overflowY:'auto', maxHeight:300, flex:1 }}>
            <table style={{ borderCollapse:'collapse', width:'100%' }}>
              <thead>
                <tr>
                  <th style={{ border:'1px solid #444', padding:4 }}>Время</th>
                  <th style={{ border:'1px solid #444', padding:4 }}>Значение</th>
                </tr>
              </thead>
              <tbody>
                {data.map((d,i)=>(
                  <tr key={i}>
                    <td style={{ border:'1px solid #444', padding:4 }}>{d.timestamp}</td>
                    <td style={{ border:'1px solid #444', padding:4 }}>{d.value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {selectedPoint && (
          <div style={{
            position:'absolute', top:100, right:100, background:'#222',
            border:'1px solid #FFD014', padding:16, borderRadius:8, color:'#FFD014'
          }}>
            <p><strong>{selectedPoint.timestamp}</strong>: {selectedPoint.value}</p>
            <button onClick={()=>{ setData(data.filter(d=>d!==selectedPoint)); setSelectedPoint(null); }}>Удалить</button>
            <button onClick={()=>{ selectedPoint.notified=true; setSelectedPoint(null); }}>Решено</button>
            <button onClick={()=>{ setSelectedPoint(null); }}>Оставить</button>
            <button onClick={()=>{ setMinBound(''); setMaxBound(''); setSelectedPoint(null); }}>Выключить уведомления</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChartModal;
