// client/src/routes/MainScreen.tsx
import React from 'react';
import { Link } from 'react-router-dom';
import useFetch from '../hooks/useFetch';

type Station = { id: number; name: string };

const MainScreen: React.FC = () => {
  const { data, error } = useFetch<Station[]>('/api/stations');

  if (!data && !error) {
    return <div>Загрузка...</div>;
  }
  if (error) {
    return <div>Ошибка: {error.toString()}</div>;
  }

  return (
    <div style={{ fontFamily: 'Roboto', padding: 20 }}>
      <h1>Станции</h1>
      <ul>
        {data!.map((s) => (
          <li key={s.id}>
            <Link to={`/station/${s.id}/objects`}>{s.name}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MainScreen;
