import React, { useState, useEffect } from 'react';
import {
  LineChart, Line,
  XAxis, YAxis,
  CartesianGrid, Tooltip
} from 'recharts';

interface Props {
  type: 'voltage' | 'current' | 'temperature';
  objectId: number;
  onClose: () => void;
}

const ChartModal: React.FC<Props> = ({ type, objectId, onClose }) => {
  // вместо фиксированных range/interval — позволим вводить arbitrary
  const nowISO = new Date().toISOString().slice(0,16); // YYYY-MM-DDThh:mm
  const [start, setStart] = useState<string>( nowISO.replace(/T.+$/, 'T00:00') );
  const [end,   setEnd]   = useState<string>( nowISO );
  const [intervalMinutes, setIntervalMinutes] = useState<number>(5);

  const [data, setData] = useState<{ timestamp: string; value: number }[]>([]);

  useEffect(() => {
    // собираем параметры
    const params = new URLSearchParams();
    params.set('start', start);
    params.set('end',   end);
    params.set('intervalMinutes', String(intervalMinutes));
    params.set('type', type);

    fetch(`/api/objects/${objectId}/data?${params}`)
      .then(r => r.json())
      .then(setData);
  }, [start, end, intervalMinutes, objectId, type]);

  // скачивание CSV, как раньше
  const downloadCSV = () => {
    const BOM = '\uFEFF';
    const header = 'timestamp;value\n';
    const rows = data.map(d => `${d.timestamp};${d.value}`).join('\n');
    const blob = new Blob([BOM + header + rows], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url; a.download = `${type}_${objectId}.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="modal">
      <div className="modal-content" style={{ background: '#000', color: '#FFD014', fontFamily: 'Roboto', padding: 20 }}>
        <button onClick={onClose} style={{ float:'right', background:'none', border:'none', color:'#FFD014', fontSize:24 }}>×</button>
        <h2>{type}</h2>

        {/* Поля для пользовательского диапазона */}
        <div style={{ display:'flex', gap:16, marginBottom:16 }}>
          <label>
            С:&nbsp;
            <input
              type="datetime-local"
              value={start}
              onChange={e => setStart(e.target.value)}
              style={{ background:'#222', color:'#FFD014', border:'1px solid #444', padding:'4px' }}
            />
          </label>
          <label>
            По:&nbsp;
            <input
              type="datetime-local"
              value={end}
              onChange={e => setEnd(e.target.value)}
              style={{ background:'#222', color:'#FFD014', border:'1px solid #444', padding:'4px' }}
            />
          </label>
          <label>
            Интервал (мин):&nbsp;
            <input
              type="number"
              value={intervalMinutes}
              min={1}
              onChange={e => setIntervalMinutes(Number(e.target.value))}
              style={{ width:60, background:'#222', color:'#FFD014', border:'1px solid #444', padding:'4px' }}
            />
          </label>
          <button onClick={downloadCSV}
            style={{ background:'#222', border:'1px solid #FFD014', color:'#FFD014', padding:'4px 8px', cursor:'pointer' }}
          >
            Скачать CSV
          </button>
        </div>

        <div style={{ display:'flex', gap:20 }}>
          <LineChart width={600} height={300} data={data}>
            <CartesianGrid stroke="#444" />
            <XAxis dataKey="timestamp" tick={{ fill:'#FFD014' }} />
            <YAxis tick={{ fill:'#FFD014' }} />
            <Tooltip
              contentStyle={{ backgroundColor:'#000', borderColor:'#FFD014' }}
              labelStyle={{ color:'#FFD014' }}
              itemStyle={{ color:'#FFD014' }}
            />
            <Line type="monotone" dataKey="value" stroke="#FFD014" dot={{ r:3 }} />
          </LineChart>

          {/* Таблица */}
          <div style={{ overflowY:'auto', maxHeight:300 }}>
            <table style={{ borderCollapse:'collapse', width:'100%' }}>
              <thead>
                <tr>
                  <th style={{border:'1px solid #444',padding:4}}>Время</th>
                  <th style={{border:'1px solid #444',padding:4}}>Значение</th>
                </tr>
              </thead>
              <tbody>
                {data.map((d,i)=>(
                  <tr key={i}>
                    <td style={{border:'1px solid #444',padding:4}}>{d.timestamp}</td>
                    <td style={{border:'1px solid #444',padding:4}}>{d.value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChartModal;
