// File: client/src/routes/ObjectSelectionPage.tsx
import React from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import useFetch from '../hooks/useFetch';
import { ObjectItem } from '../types';

type Params = { stationName: string };

const ObjectSelectionPage: React.FC = () => {
  const { stationName } = useParams<Params>();
  const navigate = useNavigate();
  const { data: objects } = useFetch<ObjectItem[]>(
    `/api/stations/${encodeURIComponent(stationName!)}/objects`
  );

  if (!objects) return <div>Загрузка...</div>;

  return (
    <div style={{ padding:20, fontFamily:'Roboto', color:'#FFD014' }}>
      <Link to="/">← Главный экран</Link>
      <h1>Объекты «{stationName}»</h1>
      <div style={{
        display:'grid',
        gridTemplateColumns:'repeat(auto-fill,minmax(200px,1fr))',
        gap:16
      }}>
        {objects.map(o => (
          <div
            key={o.id}
            onClick={() => navigate(`/object/${o.id}`)}
            style={{
              padding:16,
              background:'#111',
              borderRadius:8,
              cursor:'pointer',
              position:'relative'
            }}
          >
            <h2 style={{ margin:0 }}>{o.name}</h2>
            <p style={{ color:'#ccc' }}>{o.description}</p>
            {o.hasAlert && <span style={{
              position:'absolute', top:8, right:8
            }}>⚠️</span>}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ObjectSelectionPage;
