import React from 'react';
import { useNavigate } from 'react-router-dom';
import useFetch from '../hooks/useFetch';
import { Station } from '../types';

const MainScreen: React.FC = () => {
  const navigate = useNavigate();
  const { data: stations } = useFetch<Station[]>('/api/stations');

  if (!stations) {
    return <div style={{ fontFamily: 'Roboto, sans-serif', color: '#FFD014' }}>Загрузка...</div>;
  }

  return (
    <div style={{
      fontFamily: 'Roboto, sans-serif',
      backgroundColor: '#000',
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      padding: '20px',
      color: '#FFD014'
    }}>
      <h1 style={{ marginBottom: '16px' }}>Станции</h1>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
        gap: '16px'
      }}>
        {stations.map(station => (
          <div
            key={station.id}
            onClick={() => navigate(`/location/${station.id}/select`)}
            style={{
              backgroundColor: '#111',
              border: '1px solid #FFD014',
              borderRadius: '8px',
              padding: '32px',
              cursor: 'pointer',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between',
              height: '100%'
            }}
          >
            <h2 style={{ margin: '0 0 8px', fontSize: '1.25rem' }}>{station.name}</h2>
            <p style={{ margin: 0, fontSize: '0.9rem' }}>
              {station.hasAlert ? '⚠️ Есть тревога' : 'Тревог нет'}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MainScreen;
